import { useState } from "react";
import BookshelfChanger from "./bookshelfChanger";
import PropTypes from "prop-types";

const Book = ({bookRecord}) => {
    const [book, setBook] = useState({bookRecord})
    
    const HandleShelfChange = (shelf) => {
        book.shelf = shelf;
        setBook(book);
    };

    return (

            <div className="book">
                <div className="book-top">
                    <div
                    className="book-cover"
                    style={{
                        width: 128,
                        height: 188
                    }}
                    ></div>
                    <BookshelfChanger currentStatus={bookRecord.shelf} onChangeShelf={HandleShelfChange}/>
                </div>
                <div className="book-title">{bookRecord.title}</div>
                <div className="book-authors">{bookRecord.author}</div>
            </div>
    )
};

Book.propTypes = {
    book: PropTypes.object.isRequired
  };

export default Book;
